import React from 'react';
import { Card, CardContent, Typography, CardMedia, CardActionArea, Chip, Stack } from '@mui/material';
import { useNavigate } from 'react-router-dom';

export default function ProductCard({ product }) {
  const navigate = useNavigate();

  // Placeholder si le lien n'est pas une vraie image
  const imageUrl =
    product.link && (product.link.endsWith('.jpg') || product.link.endsWith('.png'))
      ? product.link
      : '/placeholder.jpg';

  // Exemple : couleurs (tu peux remplacer par product.colors si tu as ce champ)
  const colors = product.colors || ['Noir', 'Blanc'];

  return (
    <Card sx={{ maxWidth: 300, m: 1, boxShadow: 3 }}>
      <CardActionArea onClick={() => navigate(`/product/${product.id}`)}>
        <CardMedia
          component="img"
          height="180"
          image={imageUrl}
          alt={product.name}
        />
        <CardContent>
          <Typography variant="h6">{product.name}</Typography>
          <Typography variant="subtitle1" color="text.secondary">{product.price}</Typography>
          <Stack direction="row" spacing={1} sx={{ mt: 1 }}>
            {colors.map((c, idx) => (
              <Chip key={idx} label={c} size="small" color="primary" />
            ))}
          </Stack>
        </CardContent>
      </CardActionArea>
    </Card>
  );
}
