import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { Card, CardContent, Typography, Button, CardMedia } from "@mui/material";
import { useContext } from "react";
import { CartContext } from "../contexts/CartContext";

const testProducts = [
  { id: 1, name: "Air Max 90", price: 120, link: "/placeholder.jpg" },
  { id: 2, name: "Superstar", price: 100, link: "/placeholder.jpg" },
  { id: 3, name: "Cortez", price: 110, link: "/placeholder.jpg" }
];

export default function ProductDetail() {
  const { id } = useParams();
  const product = testProducts.find((p) => p.id === parseInt(id));
  const { addToCart } = useContext(CartContext);

  if (!product) return <div>Produit non trouvé</div>;

  return (
    <Card sx={{ maxWidth: 400, margin: "20px auto" }}>
      <CardMedia
        component="img"
        height="300"
        image={product.link}
        alt={product.name}
      />
      <CardContent>
        <Typography variant="h5">{product.name}</Typography>
        <Typography variant="body1">{product.price} €</Typography>
        <Button variant="contained" onClick={() => addToCart(product)}>
          Ajouter au panier
        </Button>
      </CardContent>
    </Card>
  );
}
