import axios from "axios";

const API = axios.create({ 
  baseURL: process.env.REACT_APP_API_URL || "http://localhost:4001"
});

export const getProducts = () => API.get("/products");
export const getProduct = (id) => API.get(`/products/${id}`);

export default API;
