import { useState, useEffect } from "react";

const COOKIE_NAME = "sneakers_cookie_consent";

export default function CookieBanner() {
  const [visible, setVisible] = useState(false);
  const [consent, setConsent] = useState({
    analytics: false,
    marketing: false,
    personalization: false,
  });

  useEffect(() => {
    const saved = localStorage.getItem(COOKIE_NAME);
    if (!saved) setVisible(true);
  }, []);

  const handleAccept = () => {
    localStorage.setItem(COOKIE_NAME, JSON.stringify(consent));
    setVisible(false);
    // Initialiser GA, Pixels, etc. si consent true
  };

  const handleChange = (e) => {
    setConsent({ ...consent, [e.target.name]: e.target.checked });
  };

  if (!visible) return null;

  return (
    <div style={{
      position: "fixed", bottom: 0, left: 0, right: 0,
      background: "#111", color: "#fff", padding: 20, zIndex: 1000
    }}>
      <p>Nous utilisons des cookies pour améliorer votre expérience et à des fins marketing. Gérez vos préférences :</p>
      <label>
        <input type="checkbox" name="analytics" checked={consent.analytics} onChange={handleChange} />
        Cookies analytiques
      </label>
      <label style={{ marginLeft: 10 }}>
        <input type="checkbox" name="marketing" checked={consent.marketing} onChange={handleChange} />
        Cookies marketing
      </label>
      <label style={{ marginLeft: 10 }}>
        <input type="checkbox" name="personalization" checked={consent.personalization} onChange={handleChange} />
        Personnalisation
      </label>
      <button onClick={handleAccept} style={{ marginLeft: 20, padding: "5px 10px" }}>Valider</button>
    </div>
  );
}
