import React, { useContext, useState } from "react";
import { CartContext } from "../contexts/CartContext";
import { useNavigate } from "react-router-dom";

export default function Checkout() {
  const { cart, subtotal, clearCart } = useContext(CartContext);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  if (!cart.length) return <div>Votre panier est vide</div>;

  const handleCheckout = async () => {
    setLoading(true);
    try {
      // 1) paiement fictif
      const payRes = await fetch("http://localhost:5000/api/payments", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ amount: subtotal })
      });
      const payment = await payRes.json();

      // 2) créer commande
      const orderRes = await fetch("http://localhost:5000/api/orders", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          items: cart,
          total: subtotal,
          payment
        })
      });
      const order = await orderRes.json();

      alert("Commande validée ✅\nNuméro: " + order.id);
      clearCart();
      navigate("/");
    } catch (e) {
      console.error(e);
      alert("Erreur pendant le paiement");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h1>Finaliser la commande</h1>
      <p>Total à payer : {subtotal.toFixed(2)} €</p>
      <button onClick={handleCheckout} disabled={loading}>
        {loading ? "Paiement en cours..." : "Payer (fictif)"}
      </button>
    </div>
  );
}
