import { createContext, useState } from "react";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    const existing = cart.find(item => item.id === product.id);
    if (existing) {
      setCart(cart.map(item => item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (id) => setCart(cart.filter(item => item.id !== id));
  const clearCart = () => setCart([]);
  const updateQuantity = (id, quantity) => {
    if (quantity <= 0) removeFromCart(id);
    else setCart(cart.map(item => item.id === id ? { ...item, quantity } : item));
  };

  const subtotal = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const checkoutCart = async () => {
    try {
      if (!cart.length) throw new Error("Panier vide");

      // ⚡ Pas de paiement réel pour l'instant, simulation
      const paymentId = `PAY-${Date.now()}`;

      // Création commande
      const orderRes = await fetch("/api/orders", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId: 1, items: cart, paymentId })
      });

      if (!orderRes.ok) throw new Error("Erreur création commande");
      const orderData = await orderRes.json();

      clearCart();
      return orderData.order;
    } catch (err) {
      console.error("Erreur lors du checkout :", err);
      throw err;
    }
  };

  return (
    <CartContext.Provider value={{ cart, addToCart, removeFromCart, clearCart, updateQuantity, subtotal, checkoutCart }}>
      {children}
    </CartContext.Provider>
  );
};
