import React, { useEffect, useState } from "react";
import { getProducts, createProduct, deleteProduct, getImages } from "../api/api";
import { Typography, TextField, Button, List, ListItem, Divider } from "@mui/material";

export default function AdminDashboard({ user }) {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: "", price: "" });
  const token = localStorage.getItem("token");

  // Si pas admin, on ne montre rien
  if (!user || user.role !== "admin") {
    return <div style={{ padding: "2rem" }}>ðŸš« AccÃ¨s rÃ©servÃ© aux administrateurs</div>;
  }

  const fetchProducts = async () => {
    try {
      const res = await getProducts();
      const productsWithImages = await Promise.all(
        res.data.map(async (p) => {
          const imgRes = await getImages(p.id);
          return { ...p, link: imgRes.data[0]?.url || "/placeholder.jpg" };
        })
      );
      setProducts(productsWithImages);
    } catch (err) {
      console.error("Erreur fetch produits :", err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleAddProduct = async () => {
    if (!newProduct.name || !newProduct.price) return alert("Remplissez tous les champs");
    try {
      await createProduct(newProduct, token);
      setNewProduct({ name: "", price: "" });
      fetchProducts();
    } catch (err) {
      console.error(err);
      alert("Erreur ajout produit");
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteProduct(id, token);
      setProducts(products.filter(p => p.id !== id));
    } catch (err) {
      console.error(err);
      alert("Erreur suppression produit");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <Typography variant="h4">Admin Dashboard</Typography>

      <Divider sx={{ my: 2 }} />
      <Typography variant="h6">Ajouter un produit</Typography>
      <TextField
        label="Nom"
        value={newProduct.name}
        onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
        sx={{ mr: 1 }}
      />
      <TextField
        label="Prix"
        type="number"
        value={newProduct.price}
        onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
        sx={{ mr: 1 }}
      />
      <Button variant="contained" onClick={handleAddProduct}>Ajouter</Button>

      <Divider sx={{ my: 2 }} />
      <Typography variant="h6">Produits existants</Typography>
      <List>
        {products.map(p => (
          <ListItem
            key={p.id}
            secondaryAction={
              <Button color="error" onClick={() => handleDelete(p.id)}>Supprimer</Button>
            }
          >
            <img src={p.link} alt={p.name} width={50} style={{ marginRight: 10, borderRadius: 5 }} />
            {p.name} - {Number(p.price).toFixed(2)} â‚¬
          </ListItem>
        ))}
      </List>
    </div>
  );
}
