// src/components/AdminDashboard.js
import React, { useEffect, useState } from "react";
import { getProducts, createProduct, deleteProduct } from "../api/api";
import { Typography, TextField, Button, List, ListItem, Divider } from "@mui/material";

export default function AdminDashboard() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: "", price: "" });
  const token = localStorage.getItem("token");

  // Récupérer la liste des produits
  const fetchProducts = async () => {
    try {
      const res = await getProducts();
      setProducts(res.data);
    } catch (err) {
      console.error("Erreur chargement produits :", err);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  // Ajouter un produit
  const handleAddProduct = async () => {
    if (!newProduct.name || !newProduct.price) {
      return alert("Remplissez tous les champs");
    }

    try {
      await createProduct(newProduct, token);
      setNewProduct({ name: "", price: "" });
      fetchProducts();
    } catch (err) {
      console.error("Erreur ajout produit :", err);
      alert(err.response?.data?.message || "Erreur serveur");
    }
  };

  // Supprimer un produit
  const handleDelete = async (id) => {
    if (!window.confirm("Voulez-vous vraiment supprimer ce produit ?")) return;
    try {
      await deleteProduct(id, token);
      setProducts(products.filter((p) => p.id !== id));
    } catch (err) {
      console.error("Erreur suppression produit :", err);
      alert(err.response?.data?.message || "Erreur serveur");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <Typography variant="h4">Admin Dashboard</Typography>

      <Divider sx={{ my: 2 }} />

      <Typography variant="h6">Ajouter un produit</Typography>
      <TextField
        label="Nom"
        value={newProduct.name}
        onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
        sx={{ mr: 1 }}
      />
      <TextField
        label="Prix"
        type="number"
        value={newProduct.price}
        onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
        sx={{ mr: 1 }}
      />
      <Button variant="contained" onClick={handleAddProduct}>
        Ajouter
      </Button>

      <Divider sx={{ my: 2 }} />

      <Typography variant="h6">Produits existants</Typography>
      <List>
        {products.map((p) => (
          <ListItem
            key={p.id}
            secondaryAction={
              <Button color="error" onClick={() => handleDelete(p.id)}>
                Supprimer
              </Button>
            }
          >
            {p.name} - {Number(p.price).toFixed(2)} €
          </ListItem>
        ))}
      </List>
    </div>
  );
}
