import React from 'react';
import { Card, CardContent, Typography, CardMedia, CardActionArea } from '@mui/material';
import { useNavigate } from 'react-router-dom';

export default function ProductCard({ product }) {
  const navigate = useNavigate();

  // ✅ Ici : on définit l'image à utiliser
  const imageUrl = product.link && (product.link.endsWith('.jpg') || product.link.endsWith('.png'))
                   ? product.link 
                   : '/placeholder.jpg';

  return (
    <Card>
      <CardActionArea onClick={() => navigate(`/product/${product.id}`)}>
        <CardMedia
          component="img"
          height="140"
          image={imageUrl} // ← on utilise imageUrl ici
          alt={product.nom}
        />
        <CardContent>
          <Typography variant="h6">{product.nom}</Typography>
          <Typography variant="body2" color="text.secondary">{product.prix} €</Typography>
        </CardContent>
      </CardActionArea>
    </Card>
  );
}
