import React, { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import { getProduct, resolveImg } from "../api/api";
import { CartContext } from "../contexts/CartContext";

export default function ProductDetail() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    let mounted = true;
    getProduct(id)
      .then(res => {
        const p = res.data;
        if (mounted) {
          setProduct({
            id: p.id,
            name: p.name || p.nom,
            price: Number(p.price || p.prix || 0),
            link: resolveImg(p.link)
          });
        }
      })
      .catch(console.error);
    return () => { mounted = false; };
  }, [id]);

  if (!product) return <div>Chargement...</div>;

  return (
    <div className="product-detail" style={{ padding: "1rem" }}>
      <img
        src={product.link}
        alt={product.name}
        style={{ width: 300, maxWidth: "100%", borderRadius: 8 }}
        onError={(e) => { e.currentTarget.src = resolveImg(null); }}
      />
      <h2 style={{ marginTop: 16 }}>{product.name}</h2>
      <p style={{ fontSize: "1.1rem", fontWeight: 600 }}>{product.price.toFixed(2)} â‚¬</p>
      <button onClick={() => addToCart(product)}>Ajouter au panier</button>
    </div>
  );
}
