import React from "react";
import { Link } from "react-router-dom";
import { useContext } from "react";
import { CartContext } from "../contexts/CartContext";

export default function Header() {
  const { cart } = useContext(CartContext);
  return (
    <header style={{ display: "flex", justifyContent: "space-between", padding: "1rem 2rem", background: "#111", color: "#fff" }}>
      <Link to="/" style={{ color: "#fff", textDecoration: "none", fontWeight: "bold" }}>Sneakers Shop</Link>
      <nav style={{ display: "flex", gap: "1rem" }}>
        <Link to="/cart" style={{ color: "#fff" }}>Panier ({cart.length})</Link>
        <Link to="/login" style={{ color: "#fff" }}>Connexion</Link>
        <Link to="/legal" style={{ color: "#fff" }}>Mentions l√©gales</Link>
      </nav>
    </header>
  );
}
