import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { CartContext } from "../contexts/CartContext";

export default function ProductCard({ product }) {
  const { addToCart } = useContext(CartContext);

  const src =
    product?.link && (product.link.startsWith("http") || product.link.startsWith("/images/"))
      ? product.link
      : "/images/placeholder.jpg";

  const handleError = (e) => {
    if (e?.currentTarget) e.currentTarget.src = "/images/placeholder.jpg";
  };

  return (
    <div style={{ border: "1px solid #ddd", borderRadius: 8, padding: "1rem", textAlign: "center", background: "#fff" }}>
      <Link to={`/product/${product.id}`}>
        <img src={src} alt={product.name} style={{ width: "100%", borderRadius: 8 }} onError={handleError} />
      </Link>
      <h3 style={{ marginTop: 12 }}>{product.name}</h3>
      <p style={{ fontWeight: "bold" }}>{Number(product.price).toFixed(2)} â‚¬</p>
      <button onClick={() => addToCart(product)}>Ajouter au panier</button>
    </div>
  );
}
