import axios from "axios";

const baseURL = "http://localhost:5050/api"; // adapte si ton backend a pris 5000/5060
export const API = axios.create({ baseURL });

export function setToken(token){
  if (token) API.defaults.headers.common["Authorization"] = `Bearer ${token}`;
  else delete API.defaults.headers.common["Authorization"];
}

// Produits
export const getProducts   = (params={}) => API.get("/products", { params });
export const getProduct    = (id)       => API.get(`/products/${id}`);
export const createProduct = (data)     => API.post("/products", data);
export const updateProduct = (id,data)  => API.put(`/products/${id}`, data);
export const deleteProduct = (id)       => API.delete(`/products/${id}`);
export const patchStock    = (id,sku,delta) => API.patch(`/products/${id}/stock`, { sku, delta });

// Auth
export const login    = (data) => API.post("/auth/login", data);
export const register = (data) => API.post("/auth/register", data);

// Commandes & paiements
export const createPayment = (data) => API.post("/payments", data);
export const createOrder   = (data) => API.post("/orders", data);
export const listOrders    = ()     => API.get("/orders");
export const setOrderStatus= (id,status)=> API.patch(`/orders/${id}/status`, { status });

// RGPD
export const getPrivacy = () => API.get("/privacy");
export const gdprExport = () => API.post("/gdpr/export");
export const gdprDelete = () => API.post("/gdpr/delete");

export default API;
