import React, { useContext, useState } from "react";
import { CartContext } from "../contexts/CartContext";
import { Button, Typography, Box } from "@mui/material";

export default function CartPage() {
  const { cart, subtotal, removeFromCart, updateQuantity, clearCart, checkoutCart } = useContext(CartContext);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  if (!cart.length) return <Typography variant="h6">Votre panier est vide</Typography>;

  const handleCheckout = async () => {
    try {
      setLoading(true);
      const order = await checkoutCart();
      setMessage(`✅ Commande réussie ! ID: ${order.order.id}`);
    } catch (err) {
      setMessage("❌ Erreur lors de la commande");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Typography variant="h4" gutterBottom>Mon panier</Typography>
      {cart.map(item => (
        <Box key={item.id} sx={{ mb: 2, p:2, border: '1px solid #ddd', borderRadius: 2 }}>
          <Typography variant="h6">{item.name}</Typography>
          <Typography>{item.price.toFixed(2)} € × {item.quantity}</Typography>
          <Button onClick={() => updateQuantity(item.id, item.quantity - 1)}>-</Button>
          <Button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</Button>
          <Button color="error" onClick={() => removeFromCart(item.id)}>Supprimer</Button>
        </Box>
      ))}
      <Typography variant="h5">Total : {subtotal.toFixed(2)} €</Typography>
      <Button variant="outlined" onClick={clearCart} sx={{ mr:2 }}>Vider le panier</Button>
      <Button variant="contained" onClick={handleCheckout} disabled={loading}>
        {loading ? "Paiement en cours..." : "Passer commande / Paiement"}
      </Button>
      {message && <Typography sx={{ mt: 2 }}>{message}</Typography>}
    </Box>
  );
}
