// src/pages/Checkout.jsx
import React, { useContext, useState } from "react";
import { CartContext } from "../contexts/CartContext";
import { createOrder, createPayment } from "../api/api";

export default function Checkout() {
  const { cart, clearCart } = useContext(CartContext);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState("");

  const total = cart.reduce((sum, item) => sum + item.price, 0);

  const handlePayment = async () => {
    setLoading(true);
    setError("");
    try {
      // Créer la commande
      const orderRes = await createOrder({ items: cart });
      // Simuler paiement (ou utiliser Stripe / PayPal ici)
      await createPayment({ orderId: orderRes.data.id, amount: total });
      setSuccess(true);
      clearCart();
    } catch (err) {
      setError("Erreur lors du paiement.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  if (success) return <div style={{ padding: 20 }}><h2>Paiement réussi ✅</h2></div>;

  return (
    <div style={{ padding: 20 }}>
      <h2>Checkout</h2>
      {cart.length === 0 ? (
        <p>Votre panier est vide.</p>
      ) : (
        <>
          <ul>
            {cart.map((item) => (
              <li key={item.id}>{item.name} - {item.price.toFixed(2)} €</li>
            ))}
          </ul>
          <p><strong>Total :</strong> {total.toFixed(2)} €</p>
          {error && <p style={{ color: "red" }}>{error}</p>}
          <button onClick={handlePayment} disabled={loading}>
            {loading ? "Paiement en cours..." : "Payer"}
          </button>
        </>
      )}
    </div>
  );
}
