import React, { useState, useEffect } from "react";

export default function CookieBanner() {
  const [showBanner, setShowBanner] = useState(false);
  const [consent, setConsent] = useState({
    analytics: false,
    marketing: false,
    personalization: false,
  });

  useEffect(() => {
    const storedConsent = localStorage.getItem("cookieConsent");
    if (!storedConsent) setShowBanner(true);
    else setConsent(JSON.parse(storedConsent));
  }, []);

  const handleSave = () => {
    localStorage.setItem("cookieConsent", JSON.stringify(consent));
    setShowBanner(false);
    // Tu peux ici activer les scripts analytics / marketing
    if (consent.analytics) console.log("Google Analytics activé");
    if (consent.marketing) console.log("Marketing activé");
    if (consent.personalization) console.log("Personnalisation activée");
  };

  if (!showBanner) return null;

  return (
    <div style={{
      position: "fixed",
      bottom: 0,
      left: 0,
      right: 0,
      background: "#111",
      color: "#fff",
      padding: "20px",
      zIndex: 9999,
    }}>
      <h4>Nous utilisons des cookies</h4>
      <p>Gérez vos préférences :</p>
      <div style={{ display: "flex", gap: "10px", flexWrap: "wrap" }}>
        <label>
          <input
            type="checkbox"
            checked={consent.analytics}
            onChange={(e) =>
              setConsent({ ...consent, analytics: e.target.checked })
            }
          />
          Analytiques (Google Analytics)
        </label>
        <label>
          <input
            type="checkbox"
            checked={consent.marketing}
            onChange={(e) =>
              setConsent({ ...consent, marketing: e.target.checked })
            }
          />
          Marketing (Facebook / AdWords)
        </label>
        <label>
          <input
            type="checkbox"
            checked={consent.personalization}
            onChange={(e) =>
              setConsent({ ...consent, personalization: e.target.checked })
            }
          />
          Personnalisation (recommandations)
        </label>
      </div>
      <button
        onClick={handleSave}
        style={{
          marginTop: "10px",
          padding: "10px 15px",
          border: "none",
          borderRadius: "5px",
          backgroundColor: "#ff3d00",
          color: "#fff",
          cursor: "pointer",
        }}
      >
        Enregistrer mes choix
      </button>
    </div>
  );
}
