// src/pages/CartPage.jsx
import React, { useContext } from "react";
import { CartContext } from "../contexts/CartContext";
import { Link, useNavigate } from "react-router-dom";

export default function CartPage() {
  const { cart, removeFromCart, updateQuantity, total } = useContext(CartContext);
  const navigate = useNavigate();

  if (cart.length === 0) return <div className="container"><h2>Votre panier est vide</h2></div>;

  return (
    <div className="container">
      <h1>Panier</h1>
      {cart.map((item) => (
        <div key={item.id} style={{ display: "flex", gap: "1rem", alignItems: "center", marginBottom: "1rem" }}>
          <img src={item.image} alt={item.name} width={80} />
          <div>
            <h3>{item.name}</h3>
            <p>{item.price.toFixed(2)} €</p>
            <input
              type="number"
              min={1}
              value={item.quantity}
              onChange={(e) => updateQuantity(item.id, Number(e.target.value))}
            />
            <button onClick={() => removeFromCart(item.id)}>Supprimer</button>
          </div>
        </div>
      ))}
      <h2>Total: {total.toFixed(2)} €</h2>
      <button onClick={() => navigate("/checkout")}>Passer à la caisse</button>
    </div>
  );
}
