import React, { useState } from "react";
import ProductList from "./components/ProductList";
import ProductDetail from "./components/ProductDetail";
import AdminDashboard from "./components/AdminDashboard";
import Login from "./components/Login";
import { Button } from "@mui/material";

function App() {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [adminMode, setAdminMode] = useState(false);
  const [user, setUser] = useState(null); // nouvel état user

  if (adminMode) {
    // Si pas connecté → afficher login
    if (!user) {
      return (
        <>
          <Button onClick={() => setAdminMode(false)}>Retour Boutique</Button>
          <Login onLogin={setUser} />
        </>
      );
    }

    // Si connecté en admin
    return (
      <>
        <Button onClick={() => setAdminMode(false)}>Retour Boutique</Button>
        <Button
          color="error"
          onClick={() => {
            localStorage.removeItem("token");
            setUser(null);
          }}
        >
          Déconnexion
        </Button>
        <AdminDashboard />
      </>
    );
  }

  // Mode "détail produit"
  if (selectedProduct) {
    return (
      <>
        <Button onClick={() => setSelectedProduct(null)}>← Retour</Button>
        <Button onClick={() => setAdminMode(true)}>Admin</Button>
        <ProductDetail productId={selectedProduct} />
      </>
    );
  }

  // Mode "catalogue"
  return (
    <>
      <Button onClick={() => setAdminMode(true)}>Admin</Button>
      <ProductList onSelect={setSelectedProduct} />
    </>
  );
}

export default App;
