// src/contexts/CartContext.js
import { createContext, useState } from "react";
import { pay, createOrder } from "../api";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem('cart') || '[]');
    } catch { return []; }
  });

  // Persist cart
  const persist = (nextCart) => {
    setCart(nextCart);
    localStorage.setItem('cart', JSON.stringify(nextCart));
  };

  const addToCart = (product) => {
    const existing = cart.find(item => item.id === product.id);
    if (existing) {
      persist(cart.map(item =>
        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
      ));
    } else {
      persist([...cart, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (id) => {
    persist(cart.filter(item => item.id !== id));
  };

  const clearCart = () => {
    persist([]);
  };

  const updateQuantity = (id, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(id);
    } else {
      persist(cart.map(item =>
        item.id === id ? { ...item, quantity: newQuantity } : item
      ));
    }
  };

  const subtotal = cart.reduce((sum, item) => {
    const price = typeof item.price === 'number' ? item.price :
      Number(String(item.price).replace(/\s/g, '').replace('€','').replace(',', '.')) || 0;
    return sum + price * (item.quantity || 1);
  }, 0);

  // checkout: appeler /api/payments puis /api/orders
  const checkoutCart = async ({ userId = null } = {}) => {
    // payment
    const payRes = await pay({ amount: subtotal });
    if (!payRes || !payRes.data || !payRes.data.transactionId) {
      throw new Error('Paiement échoué');
    }
    const payment = payRes.data;

    // format items expected par le backend
    const items = cart.map(it => ({
      product_id: it.product_id || it.id,
      variant_id: it.variant_id || it.variantId || null,
      quantity: it.quantity,
      price: typeof it.price === 'number' ? it.price :
             Number(String(it.price).replace(/\s/g, '').replace('€','').replace(',', '.')) || 0
    }));

    const orderRes = await createOrder({
      userId,
      items,
      total: subtotal,
      payment
    });

    // clear cart local
    clearCart();
    return orderRes.data; // { success: true, order: {...} }
  };

  return (
    <CartContext.Provider value={{
      cart,
      addToCart,
      removeFromCart,
      clearCart,
      checkoutCart,
      updateQuantity,
      subtotal
    }}>
      {children}
    </CartContext.Provider>
  );
};
