import React, { useContext, useState } from "react";
import { CartContext } from "../contexts/CartContext";
import { createOrder, createPayment } from "../api/api";

export default function Checkout() {
  const { cart, totalPrice, clearCart } = useContext(CartContext);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  const handlePayment = async () => {
    setLoading(true);
    try {
      // Crée la commande
      const orderRes = await createOrder({ items: cart, total: totalPrice });
      // Simule paiement
      await createPayment({ orderId: orderRes.data.id, amount: totalPrice });
      clearCart();
      setSuccess(true);
    } catch (err) {
      console.error(err);
      alert("Erreur lors du paiement");
    }
    setLoading(false);
  };

  if (success) return <div style={{ padding: "2rem" }}>✅ Votre commande a été passée !</div>;

  return (
    <div style={{ padding: "2rem" }}>
      <h1>Checkout</h1>
      <ul>
        {cart.map((p) => (
          <li key={p.id}>{p.name} x {p.quantity} : {(p.price * p.quantity).toFixed(2)} €</li>
        ))}
      </ul>
      <h2>Total : {totalPrice.toFixed(2)} €</h2>
      <button onClick={handlePayment} disabled={loading}>
        {loading ? "Paiement en cours..." : "Payer maintenant"}
      </button>
    </div>
  );
}
