import React, { useEffect, useState } from "react";
import { getProducts } from "../api/api";
import { Link } from "react-router-dom";

export default function Home() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    async function fetchProducts() {
      const res = await getProducts();
      // Assure que price est un nombre
      const formatted = res.data.map(p => ({ ...p, price: Number(p.price) }));
      setProducts(formatted);
    }
    fetchProducts();
  }, []);

  return (
    <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))", gap: 20, padding: 20 }}>
      {products.map(p => (
        <div key={p.id} style={{ border: "1px solid #ccc", borderRadius: 10, padding: 10, backgroundColor: "#fff" }}>
          <img src={p.imageUrl} alt={p.name} style={{ width: "100%", height: 150, objectFit: "cover" }} />
          <h3>{p.name}</h3>
          <p>{p.price.toFixed(2)} â‚¬</p>
          <Link to={`/product/${p.id}`}>Voir le produit</Link>
        </div>
      ))}
    </div>
  );
}
