import { useEffect, useState } from "react";
import { getProducts, patchStock, listOrders, setOrderStatus } from "../api/api";

export default function SellerPage(){
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);

  const load = async () => {
    const [p, o] = await Promise.all([getProducts(), listOrders()]);
    setProducts(p.data);
    setOrders(o.data);
  };

  useEffect(()=>{ load(); },[]);

  const bump = async (pid, sku, delta) => {
    await patchStock(pid, sku, delta);
    setProducts(ps => ps.map(p => p.id===pid ? { ...p, variants: (p.variants||[]).map(v => v.sku===sku ? { ...v, stock: v.stock + delta } : v) } : p));
  };

  const move = async (id, status) => {
    await setOrderStatus(id, status);
    setOrders(o => o.map(x => x.id===id ? { ...x, status } : x));
  };

  return (
    <div style={{ padding:20 }}>
      <h2>Vendeur — Stocks</h2>
      {products.slice(0,30).map(p=>(
        <details key={p.id} style={{ marginBottom:8 }}>
          <summary>{p.name} — {p.category}</summary>
          {(p.variants||[]).map(v=>(
            <div key={v.sku} style={{ display:"flex", gap:8, alignItems:"center", margin:"4px 0" }}>
              <span>SKU {v.sku} · T{v.size} · stock {v.stock}</span>
              <button onClick={()=>bump(p.id, v.sku, +1)}>+1</button>
              <button onClick={()=>bump(p.id, v.sku, -1)}>-1</button>
            </div>
          ))}
        </details>
      ))}

      <h2>Vendeur — Commandes</h2>
      <ul>
        {orders.map(o=>(
          <li key={o.id} style={{ margin:"6px 0" }}>
            #{o.id} — {o.status} — {o.total}€
            <select value={o.status} onChange={e=>move(o.id, e.target.value)} style={{ marginLeft:10 }}>
              <option value="paid">paid</option>
              <option value="processing">processing</option>
              <option value="shipped">shipped</option>
              <option value="delivered">delivered</option>
              <option value="cancelled">cancelled</option>
            </select>
          </li>
        ))}
      </ul>
    </div>
  );
}
