import React, { useContext } from "react";
import { CartContext } from "../contexts/CartContext";

export default function ProductCard({ product }) {
  const { addToCart } = useContext(CartContext);

  return (
    <div style={{
      border: "1px solid #ddd", borderRadius: 8, padding: 16,
      display: "flex", flexDirection: "column", alignItems: "center",
      boxShadow: "0 4px 8px rgba(0,0,0,0.1)"
    }}>
      <img src={product.link} alt={product.name} style={{ width: "100%", height: 200, objectFit: "cover", marginBottom: 8 }} />
      <h3>{product.name}</h3>
      <p style={{ fontWeight: "bold" }}>{Number(product.price).toFixed(2)} â‚¬</p>
      <button onClick={() => addToCart(product)}
        style={{
          padding: "8px 16px", backgroundColor: "#00b894",
          color: "#fff", border: "none", borderRadius: 4, cursor: "pointer"
        }}>
        Ajouter au panier
      </button>
    </div>
  );
}
