import { createContext, useState } from "react";
import { createOrder, createPayment } from "../api/api";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    const existing = cart.find(item => item.id === product.id);
    if (existing) {
      setCart(cart.map(item =>
        item.id === product.id
          ? { ...item, quantity: Number(item.quantity) + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (id) => setCart(cart.filter(item => item.id !== id));
  const clearCart = () => setCart([]);
  const updateQuantity = (id, quantity) => {
    if (quantity <= 0) removeFromCart(id);
    else setCart(cart.map(item => item.id === id ? { ...item, quantity } : item));
  };

  const subtotal = cart.reduce((sum, item) => sum + Number(item.price) * Number(item.quantity), 0);

  const checkoutCart = async () => {
    try {
      const payment = await createPayment({ amount: subtotal });
      const order = await createOrder({ userId: 1, items: cart, paymentId: payment.data.id });
      clearCart();
      return order.data;
    } catch (err) {
      console.error("Erreur checkout :", err);
      throw err;
    }
  };

  return (
    <CartContext.Provider value={{
      cart, addToCart, removeFromCart, clearCart, updateQuantity, subtotal, checkoutCart
    }}>
      {children}
    </CartContext.Provider>
  );
};
