import axios from "axios";

const API = axios.create({ baseURL: "http://localhost:4001" });

// Auth
export const login = (data) => API.post("/auth/login", data);
export const register = (data) => API.post("/auth/register", data);

// Produits
export const getProducts = (token) =>
  API.get("/products", { headers: token ? { Authorization: `Bearer ${token}` } : {} });

export const createProduct = (product, token) =>
  API.post("/products", product, { headers: { Authorization: `Bearer ${token}` } });

export const deleteProduct = (id, token) =>
  API.delete(`/products/${id}`, { headers: { Authorization: `Bearer ${token}` } });

// Panier & commande
export const addToCart = (productId, quantity, token) =>
  API.post("/cart", { productId, quantity }, { headers: { Authorization: `Bearer ${token}` } });

export const getCart = (token) =>
  API.get("/cart", { headers: { Authorization: `Bearer ${token}` } });

export const checkoutCart = (token) =>
  API.post("/cart/checkout", {}, { headers: { Authorization: `Bearer ${token}` } });

export default API;
