import React, { useEffect, useState } from "react";
import { getProducts } from "../api/api";
import { Link } from "react-router-dom";

export default function Home() {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await getProducts();
        setProducts(res.data.map(p => ({ ...p, price: Number(p.price) })));
      } catch (err) {
        console.error(err);
      }
    };
    fetchProducts();
  }, []);

  const filteredProducts = products.filter(p =>
    p.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ padding: "1rem" }}>
      <input
        type="text"
        placeholder="Rechercher une sneaker..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ padding: "0.5rem", width: "100%", marginBottom: "1rem", borderRadius: 5, border: "1px solid #ccc" }}
      />

      <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill,minmax(250px,1fr))", gap: "1rem" }}>
        {filteredProducts.map(p => (
          <div key={p.id} style={{
            border: "1px solid #ddd",
            borderRadius: 10,
            overflow: "hidden",
            transition: "transform 0.2s",
            cursor: "pointer"
          }}
          onMouseEnter={e => e.currentTarget.style.transform = "scale(1.05)"}
          onMouseLeave={e => e.currentTarget.style.transform = "scale(1)"}
          >
            <Link to={`/product/${p.id}`}>
              <img src={p.images?.[0]?.url || "/placeholder.jpg"} alt={p.name} style={{ width: "100%" }} />
            </Link>
            <div style={{ padding: "0.5rem" }}>
              <h3>{p.name}</h3>
              <p style={{ fontWeight: "bold" }}>{p.price.toFixed(2)} â‚¬</p>
              {p.isNew && <span style={{ color: "red", fontWeight: "bold" }}>Nouveau</span>}
              <Link to={`/product/${p.id}`}><button style={{ width: "100%", marginTop: 5 }}>Voir le produit</button></Link>
            </div>
          </div>
        ))}
      </div>

      <h2 style={{ marginTop: "2rem" }}>Suggestions pour vous</h2>
      <div style={{ display: "flex", gap: "1rem", overflowX: "scroll", paddingBottom: 10 }}>
        {products.slice(0, 5).map(p => (
          <Link key={p.id} to={`/product/${p.id}`} style={{ minWidth: 150 }}>
            <img src={p.images?.[0]?.url || "/placeholder.jpg"} alt={p.name} style={{ width: "100%", borderRadius: 10 }} />
            <p style={{ textAlign: "center" }}>{p.name}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}
