// src/api/api.js
import axios from "axios";

// ⚠️ Pointe directement sur ton backend Node
//    Change le port si ton serveur affiche 5050 au démarrage.
const baseURL = "http://localhost:5000/api";

const API = axios.create({
  baseURL,
  withCredentials: false,
});

// ---- Auth header helper (UNE SEULE DÉCLARATION) ----
export function setToken(token) {
  if (token) {
    API.defaults.headers.common["Authorization"] = `Bearer ${token}`;
  } else {
    delete API.defaults.headers.common["Authorization"];
  }
}

// ---- Produits ----
export const getProducts = () => API.get("/products");
export const getProduct = (id) => API.get(`/products/${id}`);
export const getImages = (productId) => API.get(`/images/${productId}`);
export const createProduct = (data) => API.post("/products", data);            // (admin + token)
export const deleteProduct = (id) => API.delete(`/products/${id}`);            // (admin + token)

// ---- Auth ----
export const login = (data) => API.post("/auth/login", data);
export const register = (data) => API.post("/auth/register", data);

// ---- Paiements & Commandes (token requis) ----
export const createPayment = (data) => API.post("/payments", data);
export const createOrder = (data) => API.post("/orders", data);

export { API };
export default API;
