import React, { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import { getProduct, getImages } from "../api/api";
import { CartContext } from "../contexts/CartContext";

export default function ProductPage() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await getProduct(id);
        const p = { ...res.data, price: Number(res.data.price) };
        const imgRes = await getImages(p.id);
        setProduct({ ...p, images: imgRes.data });
      } catch (err) {
        console.error(err);
      }
    };
    fetchProduct();
  }, [id]);

  if (!product) return <div>Chargement...</div>;

  return (
    <div style={{ display: "flex", gap: "2rem", padding: "2rem", flexWrap: "wrap" }}>
      <img src={product.images?.[0]?.url || "/placeholder.jpg"} alt={product.name} style={{ width: "300px", borderRadius: "10px" }} />
      <div>
        <h2>{product.name}</h2>
        <p style={{ fontWeight: "bold" }}>{product.price.toFixed(2)} â‚¬</p>
        <p>{product.description}</p>
        <button onClick={() => addToCart(product)}>Ajouter au panier</button>
      </div>
    </div>
  );
}
