// src/pages/Home.jsx
import React, { useState, useEffect, useContext } from "react";
import { getProducts, getImages } from "../api/api";
import { CartContext } from "../contexts/CartContext";
import { Link } from "react-router-dom";

export default function Home() {
  const [products, setProducts] = useState([]);
  const [search, setSearch] = useState("");
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    const fetchProducts = async () => {
      const res = await getProducts();
      const prods = await Promise.all(res.data.map(async (p) => {
        const imgRes = await getImages(p.id);
        return { ...p, link: imgRes.data[0]?.url || "/placeholder.jpg" };
      }));
      setProducts(prods);
    };
    fetchProducts();
  }, []);

  const filtered = products.filter(p =>
    p.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ padding: 20 }}>
      <h1>Sneakers Shop</h1>
      <input
        type="text"
        placeholder="Rechercher..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ padding: 5, marginBottom: 20 }}
      />
      <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))", gap: 20 }}>
        {filtered.map((p) => (
          <div key={p.id} style={{ border: "1px solid #ccc", borderRadius: 10, padding: 10 }}>
            <Link to={`/product/${p.id}`}>
              <img src={p.link} alt={p.name} style={{ width: "100%", height: 150, objectFit: "cover" }} />
              <h3>{p.name}</h3>
            </Link>
            <p>{p.price.toFixed(2)} â‚¬</p>
            <button onClick={() => addToCart(p)}>Ajouter au panier</button>
          </div>
        ))}
      </div>
    </div>
  );
}
