// src/components/CookieBanner.jsx
import React, { useState, useEffect } from "react";

const COOKIE_NAME = "sneakers_cookies_consent";

export default function CookieBanner() {
  const [visible, setVisible] = useState(false);
  const [preferences, setPreferences] = useState({
    analytics: false,
    marketing: false,
  });

  useEffect(() => {
    // Vérifie si l'utilisateur a déjà donné son consentement
    const saved = localStorage.getItem(COOKIE_NAME);
    if (!saved) setVisible(true);
    else setPreferences(JSON.parse(saved));
  }, []);

  const handleSave = () => {
    localStorage.setItem(COOKIE_NAME, JSON.stringify(preferences));
    setVisible(false);

    // Ici tu peux initialiser GA, FB Pixel, etc. selon les préférences
    if (preferences.analytics) console.log("✅ Analytics activés");
    if (preferences.marketing) console.log("✅ Marketing activé");
  };

  const handleReject = () => {
    localStorage.setItem(COOKIE_NAME, JSON.stringify({ analytics: false, marketing: false }));
    setVisible(false);
  };

  if (!visible) return null;

  return (
    <div style={{
      position: "fixed",
      bottom: 0,
      left: 0,
      right: 0,
      backgroundColor: "#111",
      color: "#fff",
      padding: "20px",
      zIndex: 9999,
      display: "flex",
      flexDirection: "column",
      gap: "10px"
    }}>
      <p>
        Nous utilisons des cookies pour améliorer votre expérience, analyser le trafic et vous proposer du marketing personnalisé.
      </p>

      <div style={{ display: "flex", gap: "10px", alignItems: "center" }}>
        <label>
          <input
            type="checkbox"
            checked={preferences.analytics}
            onChange={(e) => setPreferences({ ...preferences, analytics: e.target.checked })}
          /> Analytique
        </label>
        <label>
          <input
            type="checkbox"
            checked={preferences.marketing}
            onChange={(e) => setPreferences({ ...preferences, marketing: e.target.checked })}
          /> Marketing
        </label>
      </div>

      <div style={{ display: "flex", gap: "10px" }}>
        <button onClick={handleSave} style={{ backgroundColor: "#ff3d00", color: "#fff", padding: "8px 15px", borderRadius: "5px" }}>
          Accepter la sélection
        </button>
        <button onClick={handleReject} style={{ backgroundColor: "#555", color: "#fff", padding: "8px 15px", borderRadius: "5px" }}>
          Tout refuser
        </button>
      </div>
    </div>
  );
}
