import React, { useState, useEffect } from "react";

const COOKIE_KEY = "sneakers_cookie_consent";

export default function CookieBanner() {
  const [visible, setVisible] = useState(false);
  const [consent, setConsent] = useState({
    analytics: false,
    marketing: false,
    personalization: false,
  });

  useEffect(() => {
    const saved = localStorage.getItem(COOKIE_KEY);
    if (!saved) setVisible(true);
    else setConsent(JSON.parse(saved));
  }, []);

  const saveConsent = () => {
    localStorage.setItem(COOKIE_KEY, JSON.stringify(consent));
    setVisible(false);

    // Exemple d'activation / d√©sactivation de scripts
    if (consent.analytics) {
      // activer Google Analytics
    }
    if (consent.marketing) {
      // activer pixels Facebook / AdWords
    }
    if (consent.personalization) {
      // activer recommandations produits
    }
  };

  const handleToggle = (type) => {
    setConsent((prev) => ({ ...prev, [type]: !prev[type] }));
  };

  if (!visible) return null;

  return (
    <div style={{
      position: "fixed",
      bottom: 0,
      left: 0,
      right: 0,
      backgroundColor: "#111",
      color: "#fff",
      padding: "1rem",
      zIndex: 1000,
      boxShadow: "0 -2px 10px rgba(0,0,0,0.3)"
    }}>
      <h4>üç™ Gestion des cookies</h4>
      <p>Nous utilisons des cookies pour am√©liorer votre exp√©rience et analyser notre trafic.</p>

      <div style={{ marginBottom: "1rem" }}>
        <label>
          <input
            type="checkbox"
            checked={consent.analytics}
            onChange={() => handleToggle("analytics")}
          /> Cookies analytiques (Google Analytics)
        </label>
        <br />
        <label>
          <input
            type="checkbox"
            checked={consent.marketing}
            onChange={() => handleToggle("marketing")}
          /> Cookies marketing (Facebook Pixel, AdWords)
        </label>
        <br />
        <label>
          <input
            type="checkbox"
            checked={consent.personalization}
            onChange={() => handleToggle("personalization")}
          /> Cookies de personnalisation (recommandations)
        </label>
      </div>

      <button
        onClick={saveConsent}
        style={{
          backgroundColor: "#ff3d00",
          color: "#fff",
          border: "none",
          padding: "0.5rem 1rem",
          borderRadius: "5px",
          cursor: "pointer"
        }}
      >
        Enregistrer mes choix
      </button>
    </div>
  );
}
