// src/pages/CartPage.jsx
import React, { useContext } from "react";
import { CartContext } from "../contexts/CartContext";
import { Link } from "react-router-dom";

export default function CartPage() {
  const { cart, removeFromCart, subtotal } = useContext(CartContext);

  return (
    <>
      <h1>Panier</h1>
      {cart.length === 0 ? (
        <p>Votre panier est vide.</p>
      ) : (
        <>
          <ul>
            {cart.map(item => (
              <li key={item.key} className="card" style={{ marginBottom: "0.5rem" }}>
                {item.name} {item.size ? `(Taille ${item.size})` : ""} — {item.qty} ×{" "}
                {Number(item.price).toFixed(2)} €
                <button style={{ marginLeft: 10 }} onClick={() => removeFromCart(item.key)}>
                  Supprimer
                </button>
              </li>
            ))}
          </ul>
          <p>
            Total: <b>{subtotal.toFixed(2)} €</b>
          </p>
          <Link to="/checkout">
            <button>Aller au paiement</button>
          </Link>
        </>
      )}
    </>
  );
}
