import React, { useContext } from "react";
import { CartContext } from "../contexts/CartContext";
import { Link } from "react-router-dom";

export default function CartPage() {
  const { cart, removeFromCart, updateQuantity, totalPrice } = useContext(CartContext);

  if (cart.length === 0)
    return <div style={{ padding: "2rem" }}>Votre panier est vide.</div>;

  return (
    <div style={{ padding: "2rem" }}>
      <h1>Panier</h1>
      {cart.map((p) => (
        <div key={p.id} style={{ display: "flex", alignItems: "center", gap: "1rem", marginBottom: "1rem" }}>
          <img src={p.link} alt={p.name} style={{ width: 80 }} />
          <div>
            <h3>{p.name}</h3>
            <p>{p.price.toFixed(2)} €</p>
            <input
              type="number"
              min={1}
              value={p.quantity}
              onChange={(e) => updateQuantity(p.id, parseInt(e.target.value))}
              style={{ width: 50 }}
            />
            <button onClick={() => removeFromCart(p.id)}>Supprimer</button>
          </div>
        </div>
      ))}
      <h2>Total: {totalPrice.toFixed(2)} €</h2>
      <Link to="/checkout"><button>Passer à la commande</button></Link>
    </div>
  );
}
