import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { CartContext } from "../contexts/CartContext";

export default function Header() {
  const { cart } = useContext(CartContext);
  const totalItems = cart.reduce((sum, item) => sum + Number(item.quantity), 0);

  return (
    <header style={{ padding: "1rem 2rem", display: "flex", justifyContent: "space-between", alignItems: "center", borderBottom: "2px solid #eee", backgroundColor: "#111", color: "#fff" }}>
      <h1><Link to="/" style={{ color: "#fff", textDecoration: "none" }}>Sneakers Shop</Link></h1>
      <nav>
        <Link to="/cart" style={{ color: "#fff", textDecoration: "none" }}>
          Panier ({totalItems})
        </Link>
      </nav>
    </header>
  );
}
