// src/pages/LegalPage.jsx
import React, { useState } from "react";

export default function LegalPage() {
  const [form, setForm] = useState({ email: "", message: "" });
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Ici tu peux appeler un endpoint API pour envoyer le message au DPO
    console.log("Demande DPO :", form);
    setSubmitted(true);
    setForm({ email: "", message: "" });
  };

  return (
    <div style={{ maxWidth: 900, margin: "20px auto", padding: 20 }}>
      <h1>Mentions légales & Politique de confidentialité</h1>

      <section>
        <h2>Identité de l'entreprise</h2>
        <p>
          Nom de l’entreprise : Sneakers Shop SAS<br/>
          SIRET : 123 456 789 00012<br/>
          Adresse : 10 Rue du Commerce, 75000 Paris, France<br/>
          Hébergeur : Vercel France SAS, 2 rue de la Paix, 75002 Paris<br/>
          Contact DPO : <a href="#dpo-form">dpo@sneakers-shop.com</a>
        </p>
      </section>

      <section>
        <h2>Politique de confidentialité</h2>
        <ul>
          <li>Collecte des données uniquement nécessaires à l’inscription, commandes et paiement.</li>
          <li>Consentement explicite requis pour les cookies marketing et analytiques.</li>
          <li>Durée de conservation : données clients 36 mois maximum, cookies 13 mois.</li>
          <li>Droits de l’utilisateur : accès, rectification, suppression, limitation, portabilité et opposition.</li>
          <li>Mot de passe haché avec bcrypt, accès sécurisé aux données.</li>
        </ul>
      </section>

      <section>
        <h2>Cookies</h2>
        <ul>
          <li>Essentiels : panier, session, sécurité → pas de consentement requis</li>
          <li>Analytiques : Google Analytics → consentement requis</li>
          <li>Marketing : Facebook Pixel, AdWords → consentement requis</li>
          <li>Personnalisation : recommandations → consentement requis</li>
        </ul>
      </section>

      <section>
        <h2>Conditions générales de vente (CGV) / CGU</h2>
        <ul>
          <li>Prix affichés toutes taxes comprises.</li>
          <li>Livraison estimée sous 3 à 7 jours ouvrés.</li>
          <li>Retour possible sous 14 jours, produit intact et dans son emballage d’origine.</li>
          <li>Paiement sécurisé via Stripe.</li>
        </ul>
      </section>

      <section id="dpo-form">
        <h2>Contact DPO / Exercice des droits RGPD</h2>
        {submitted ? (
          <p>✅ Votre demande a été envoyée au DPO. Nous vous répondrons sous 1 mois maximum.</p>
        ) : (
          <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
            <label>
              Votre email *
              <input
                type="email"
                name="email"
                value={form.email}
                onChange={handleChange}
                required
                style={{ width: "100%", padding: "8px", borderRadius: "5px" }}
              />
            </label>
            <label>
              Message *
              <textarea
                name="message"
                value={form.message}
                onChange={handleChange}
                required
                rows={4}
                style={{ width: "100%", padding: "8px", borderRadius: "5px" }}
              />
            </label>
            <button type="submit" style={{ width: "150px" }}>Envoyer</button>
          </form>
        )}
      </section>
    </div>
  );
}
