import React, { useContext, useState } from "react";
import { CartContext } from "../contexts/CartContext";
import { useNavigate } from "react-router-dom";

export default function Checkout() {
  const { cart, subtotal, checkoutCart } = useContext(CartContext);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  if (!cart.length) return <div style={{ padding: 20 }}>Votre panier est vide</div>;

  const handleCheckout = async () => {
    setLoading(true);
    try {
      const order = await checkoutCart();
      alert(`✅ Commande validée\nNuméro: ${order.id}`);
      navigate("/");
    } catch (err) {
      alert("Erreur pendant le paiement");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Finaliser la commande</h1>
      <p>Total à payer : {Number(subtotal).toFixed(2)} €</p>
      <button onClick={handleCheckout} disabled={loading}>
        {loading ? "Paiement en cours..." : "Payer (fictif)"}
      </button>
    </div>
  );
}
