import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { getProduct, getImages } from "../api/api";
import { useCart } from "../contexts/CartContext";

export default function ProductPage() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [images, setImages] = useState([]);
  const { addToCart } = useCart();

  useEffect(() => {
    async function fetch() {
      const res = await getProduct(id);
      setProduct({ ...res.data, price: Number(res.data.price) });
      const imgRes = await getImages(id);
      setImages(imgRes.data);
    }
    fetch();
  }, [id]);

  if (!product) return <p>Chargement...</p>;

  return (
    <div style={{ padding: 20 }}>
      <h1>{product.name}</h1>
      <p>{product.price.toFixed(2)} â‚¬</p>
      <div style={{ display: "flex", gap: 10 }}>
        {images.map((img, i) => <img key={i} src={img.url} alt="" style={{ width: 100, height: 100, objectFit: "cover" }} />)}
      </div>
      <button onClick={() => addToCart(product)}>Ajouter au panier</button>
    </div>
  );
}
