import axios from "axios";

const API = axios.create({ baseURL: process.env.REACT_APP_API_URL || "http://localhost:4000/api" });

// Auth
export const login = (data) => API.post("/auth/login", data);
export const register = (data) => API.post("/auth/register", data);

// Produits
export const getProducts = () => API.get("/products");
export const getProduct = (id) => API.get(`/products/${id}`);
export const getImages = (productId) => API.get(`/products/${productId}/images`);

// Admin
export const createProduct = (data, token) => API.post("/admin/add-product", data, { headers: { Authorization: `Bearer ${token}` } });
export const deleteProduct = (id, token) => API.delete(`/admin/delete-product/${id}`, { headers: { Authorization: `Bearer ${token}` } });

export default API;
