import React, { useEffect, useState } from "react";
import { getProducts } from "../api";        // <- via src/api/index.js fixé
import ProductCard from "./ProductCard";

export default function ProductList() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");

  useEffect(() => {
    let mounted = true;
    getProducts()
      .then((res) => { if (mounted) setItems(res.data || []); })
      .catch((e) => {
        if (mounted) setErr(e.response?.data?.message || e.message || "Erreur de chargement");
      })
      .finally(() => { if (mounted) setLoading(false); });
    return () => { mounted = false; };
  }, []);

  if (loading) return <div>Chargement…</div>;
  if (err) return <div style={{ color: "crimson" }}>Erreur : {err}</div>;
  if (!items.length) return <div>Aucun produit.</div>;

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fill, minmax(220px, 1fr))",
        gap: 16,
      }}
    >
      {items.map((p) => (
        <ProductCard key={p.id} product={p} />
      ))}
    </div>
  );
}
