// src/pages/ProductPage.jsx
import React, { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import { getProduct, getImages, getProducts } from "../api/api";
import { CartContext } from "../contexts/CartContext";
import { Link } from "react-router-dom";

export default function ProductPage() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [suggestions, setSuggestions] = useState([]);
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await getProduct(id);
        const p = res.data;
        const imgRes = await getImages(p.id);
        setProduct({
          ...p,
          price: Number(p.price),
          image: imgRes.data[0]?.url || "/placeholder.jpg"
        });

        // Suggestions (autres produits)
        const allProds = await getProducts();
        setSuggestions(allProds.data.filter(pr => pr.id !== p.id).slice(0, 4));
      } catch (err) {
        console.error(err);
      }
    };
    fetchProduct();
  }, [id]);

  if (!product) return <div>Chargement...</div>;

  return (
    <div style={{ maxWidth: 1000, margin: "2rem auto" }}>
      <div style={{ display: "flex", gap: "2rem" }}>
        <img src={product.image} alt={product.name} style={{ width: 400, borderRadius: 15 }} />
        <div>
          <h1>{product.name}</h1>
          <p style={{ fontWeight: "bold", fontSize: 20 }}>{product.price.toFixed(2)} €</p>
          <p>{product.description}</p>
          <button onClick={() => addToCart(product)}>Ajouter au panier</button>
        </div>
      </div>

      {suggestions.length > 0 && (
        <div style={{ marginTop: 40 }}>
          <h3>Vous pourriez aussi aimer</h3>
          <div style={{ display: "flex", gap: "1rem", overflowX: "auto" }}>
            {suggestions.map(s => (
              <Link key={s.id} to={`/product/${s.id}`} style={{ minWidth: 200 }}>
                <img src={s.image || "/placeholder.jpg"} alt={s.name} style={{ width: "100%", height: 150, objectFit: "cover", borderRadius: 10 }} />
                <p>{s.name}</p>
                <p>{Number(s.price).toFixed(2)} €</p>
              </Link>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
