import React, { useEffect, useState } from "react";
import { getProducts } from "../api/api";
import { Link } from "react-router-dom";

export default function Home() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await getProducts();
        setProducts(res.data.map(p => ({ ...p, price: Number(p.price) })));
      } catch (err) {
        console.error(err);
      }
    };
    fetchProducts();
  }, []);

  if (!products.length) return <div>Chargement...</div>;

  return (
    <div className="container" style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill,minmax(250px,1fr))", gap: "1rem" }}>
      {products.map(p => (
        <div key={p.id} className="product-card">
          <Link to={`/product/${p.id}`}>
            <img src={p.images?.[0]?.url || "/placeholder.jpg"} alt={p.name} />
          </Link>
          <div className="product-card-content">
            <h2>{p.name}</h2>
            <p>{p.price.toFixed(2)} â‚¬</p>
            <Link to={`/product/${p.id}`}>
              <button>Voir le produit</button>
            </Link>
          </div>
        </div>
      ))}
    </div>
  );
}
