// src/contexts/CartContext.jsx
import { createContext, useState } from "react";

export const CartContext = createContext();

const API_URL = process.env.REACT_APP_API_URL || "http://localhost:4000/api";

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    const existing = cart.find(item => item.id === product.id);
    if (existing) {
      setCart(cart.map(item =>
        item.id === product.id
          ? { ...item, quantity: Number(item.quantity) + 1 }
          : item
      ));
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const removeFromCart = (id) => setCart(cart.filter(item => item.id !== id));
  const clearCart = () => setCart([]);
  const updateQuantity = (id, quantity) => {
    if (quantity <= 0) removeFromCart(id);
    else setCart(cart.map(item => item.id === id ? { ...item, quantity } : item));
  };

  // Somme totale du panier
  const subtotal = cart.reduce((sum, item) => {
    const price = Number(item.price);
    const qty = Number(item.quantity);
    return sum + (isNaN(price) ? 0 : price) * (isNaN(qty) ? 1 : qty);
  }, 0);

  // Fonction checkout
  const checkoutCart = async () => {
    try {
      // Paiement fictif
      const paymentRes = await fetch(`${API_URL}/payments`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ amount: subtotal })
      });
      if (!paymentRes.ok) throw new Error("Erreur paiement");
      const payment = await paymentRes.json();

      // Création commande
      const orderRes = await fetch(`${API_URL}/orders`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId: 1, items: cart, paymentId: payment.id })
      });
      if (!orderRes.ok) throw new Error("Erreur création commande");
      const orderData = await orderRes.json();

      clearCart();
      return orderData;
    } catch (err) {
      console.error("Erreur lors du checkout :", err);
      throw err;
    }
  };

  return (
    <CartContext.Provider value={{
      cart,
      addToCart,
      removeFromCart,
      clearCart,
      updateQuantity,
      subtotal,
      checkoutCart
    }}>
      {children}
    </CartContext.Provider>
  );
};
