import React, { useEffect, useState } from "react";
import { getProducts, createProduct, deleteProduct } from "../api/api";

export default function AdminDashboard() {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");

  const fetchProducts = async () => {
    const res = await getProducts();
    setProducts(res.data.map(p => ({ ...p, price: Number(p.price) })));
  };

  useEffect(() => { fetchProducts(); }, []);

  const handleAdd = async () => {
    if (!name || !price) return alert("Nom et prix requis");
    await createProduct({ name, price: Number(price) });
    setName(""); setPrice("");
    fetchProducts();
  };

  const handleDelete = async (id) => {
    await deleteProduct(id);
    fetchProducts();
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>Admin Dashboard</h1>
      <h2>Ajouter un produit</h2>
      <input placeholder="Nom" value={name} onChange={e => setName(e.target.value)} />
      <input placeholder="Prix" type="number" value={price} onChange={e => setPrice(e.target.value)} />
      <button onClick={handleAdd}>Ajouter</button>

      <h2>Produits existants</h2>
      <ul>
        {products.map(p => (
          <li key={p.id}>
            {p.name} - {p.price.toFixed(2)} â‚¬
            <button onClick={() => handleDelete(p.id)}>Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
