// src/pages/login.jsx
import React, { useState } from "react";
import { login, register } from "../api/api";

export default function Login({ onLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [isRegister, setIsRegister] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const data = isRegister
        ? { email, password, first_name: firstName, last_name: lastName }
        : { email, password };
      const res = isRegister ? await register(data) : await login(data);
      if (res.data.token) {
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("role", res.data.user.role);
        onLogin(res.data.user);
      } else {
        alert("Identifiants invalides");
      }
    } catch (err) {
      alert(err.response?.data?.message || "Erreur serveur");
    }
  };

  return (
    <div className="container">
      <h1>{isRegister ? "Inscription" : "Connexion"}</h1>
      <form onSubmit={handleSubmit}>
        {isRegister && (
          <>
            <input type="text" placeholder="Prénom" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
            <input type="text" placeholder="Nom" value={lastName} onChange={(e) => setLastName(e.target.value)} required />
          </>
        )}
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input type="password" placeholder="Mot de passe" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <button type="submit">{isRegister ? "S'inscrire" : "Se connecter"}</button>
      </form>
      <p onClick={() => setIsRegister(!isRegister)} style={{ cursor: "pointer", color: "blue" }}>
        {isRegister ? "Déjà un compte ? Se connecter" : "Pas de compte ? S'inscrire"}
      </p>
    </div>
  );
}
