import React, { useEffect, useState } from "react";
import { getProducts } from "../api/api";
import { Grid, Card, CardContent, Typography, Button } from "@mui/material";
import { useNavigate } from "react-router-dom";

export default function ProductList() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getProducts()
      .then((res) => {
        // Transformation pour correspondre aux props du frontend
        const formatted = res.data.map(p => ({
          id: p.id,
          name: p.nom,
          price: p.prix,
          link: p.link || "/placeholder.jpg"
        }));
        setProducts(formatted);
      })
      .catch((err) => console.error("Erreur API :", err));
  }, []);

  if (products.length === 0) return <div>Chargement des produits...</div>;

  return (
    <Grid container spacing={2} sx={{ p: 2 }}>
      {products.map((p) => (
        <Grid item xs={12} sm={6} md={4} key={p.id}>
          <Card>
            <CardContent>
              <Typography variant="h6">{p.name}</Typography>
              <Typography variant="body2">{p.price} €</Typography>
              <Button onClick={() => navigate(`/product/${p.id}`)}>
                Voir détails
              </Button>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
}
