import React from "react";
import { Card, CardMedia, CardContent, Typography, CardActions, Button } from "@mui/material";

export default function ProductCard({ product, onAdd }) {
  const price = Number(product.price) || 0; // <-- conversion en number sécurisée

  return (
    <Card sx={{ maxWidth: 300, margin: "auto", boxShadow: 3 }}>
      <CardMedia
        component="img"
        height="200"
        image={product.link}
        alt={product.name}
      />
      <CardContent>
        <Typography gutterBottom variant="h6">
          {product.name}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {price.toFixed(2)} €
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small" variant="contained" onClick={() => onAdd(product)}>
          Ajouter au panier
        </Button>
      </CardActions>
    </Card>
  );
}
