import React, { useEffect, useState } from "react";
import { getProducts, getImages } from "../api/api";
import ProductCard from "../components/ProductCard";

export default function Home() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await getProducts();
        const prods = res.data;

        const productsWithImages = await Promise.all(
          prods.map(async (p) => {
            const imgRes = await getImages(p.id);
            return {
              id: p.id,
              name: p.name,
              price: Number(p.price),
              link: imgRes.data[0]?.url || "/placeholder.jpg"
            };
          })
        );

        setProducts(productsWithImages);
      } catch (err) {
        console.error("Erreur chargement produits:", err);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit,minmax(250px,1fr))", gap: 20, padding: 20 }}>
      {products.map(p => <ProductCard key={p.id} product={p} />)}
    </div>
  );
}
