import React, { useContext, useState } from "react";
import { CartContext } from "../contexts/CartContext";
import { createPayment } from "../api/api";

export default function Checkout() {
  const { cart, clearCart } = useContext(CartContext);
  const [message, setMessage] = useState("");
  const [client, setClient] = useState({ firstName: "", lastName: "" });

  const total = cart.reduce((sum, p) => sum + p.price, 0);
  const taxes = (total * 0.2).toFixed(2); // TVA 20%
  const finalTotal = (total + Number(taxes)).toFixed(2);

  const handlePayment = async () => {
    try {
      await createPayment({ amount: finalTotal, client });
      setMessage(`✅ Paiement réussi pour ${client.firstName} ${client.lastName} !`);
      clearCart();
    } catch (err) {
      setMessage("❌ Paiement échoué...");
      console.error(err);
    }
  };

  if (!cart.length) return <div>Votre panier est vide.</div>;

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Checkout</h2>
      <input
        placeholder="Prénom"
        value={client.firstName}
        onChange={(e) => setClient({ ...client, firstName: e.target.value })}
        style={{ display:"block", marginBottom:10, padding:5 }}
      />
      <input
        placeholder="Nom"
        value={client.lastName}
        onChange={(e) => setClient({ ...client, lastName: e.target.value })}
        style={{ display:"block", marginBottom:10, padding:5 }}
      />
      <ul>
        {cart.map(p => (
          <li key={p.id}>{p.name} - {p.price.toFixed(2)} €</li>
        ))}
      </ul>
      <p>Taxes (20%) : {taxes} €</p>
      <h3>Total à payer : {finalTotal} €</h3>
      <button onClick={handlePayment} style={{ padding:"0.5rem 1rem", transition:"0.2s", cursor:"pointer" }}
        onMouseEnter={e => e.currentTarget.style.transform="scale(1.05)"}
        onMouseLeave={e => e.currentTarget.style.transform="scale(1)"}
      >
        Payer
      </button>
      {message && <p style={{ marginTop: 10 }}>{message}</p>}
    </div>
  );
}
