// src/pages/Checkout.jsx
import React, { useContext, useState } from "react";
import { CartContext } from "../contexts/CartContext";
import { createPayment, createOrder } from "../api/api";

export default function Checkout() {
  const { cart, clearCart } = useContext(CartContext);
  const [loading, setLoading] = useState(false);

  const handlePayment = async () => {
    setLoading(true);
    try {
      const orderData = { items: cart.map(p => ({ productId: p.id, quantity: 1 })) };
      const orderRes = await createOrder(orderData);

      const paymentRes = await createPayment({ orderId: orderRes.data.id, amount: cart.reduce((a,b) => a + Number(b.price),0) * 100 });
      alert("Paiement réussi ! (Simulation Stripe test)");

      clearCart();
    } catch (err) {
      alert("Erreur lors du paiement : " + err.message);
    }
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: 600, margin: "2rem auto", backgroundColor: "#fff", padding: 20, borderRadius: 15 }}>
      <h1>Checkout</h1>
      {cart.length === 0 ? <p>Votre panier est vide.</p> : (
        <>
          <ul>
            {cart.map(p => (
              <li key={p.id}>{p.name} - {Number(p.price).toFixed(2)} €</li>
            ))}
          </ul>
          <p>Total: {cart.reduce((a,b)=>a+Number(b.price),0).toFixed(2)} €</p>
          <button onClick={handlePayment} disabled={loading}>
            {loading ? "Traitement..." : "Payer"}
          </button>
        </>
      )}
    </div>
  );
}
