import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useContext } from "react";
import { CartContext } from "../contexts/CartContext";

export default function Header() {
  const { cart } = useContext(CartContext);
  const [search, setSearch] = useState("");
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    navigate(`/?search=${encodeURIComponent(search)}`);
    setSearch("");
  };

  return (
    <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "15px 30px", backgroundColor: "#111", color: "#fff" }}>
      <Link to="/" style={{ fontWeight: "bold", fontSize: "1.5rem", color: "#fff" }}>Sneakers Shop</Link>

      <form onSubmit={handleSearch} style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
        <input
          type="text"
          placeholder="Rechercher des sneakers..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ padding: "5px 10px", borderRadius: 5, border: "none", width: 200 }}
        />
        <button type="submit" style={{ padding: "5px 10px", borderRadius: 5, border: "none", cursor: "pointer" }}>üîç</button>
      </form>

      <div>
        <Link to="/cart" style={{ marginLeft: 20, color: "#fff" }}>üõí {cart.length}</Link>
        <Link to="/login" style={{ marginLeft: 20, color: "#fff" }}>üë§</Link>
      </div>
    </header>
  );
}
